INSERT INTO `binhluans` VALUES (2, 1, 8, 'Thật tuyệt vời thiên nhiên ở đây thật hùng vĩ, nó đẹp hơn rất nhiều so với tôi tưởng tượng !', 5, 1, 1, '2020-12-20 02:08:26', '2020-12-20 02:09:51');
